"""Blogly application."""
"""Eldy Deines"""
"""App incorporates SQL Alchemy"""


"""Import necessary libraries and models"""
from flask import Flask, request, render_template, redirect, flash
from models import db, connect_db, User, Post


"""Configure flask object and set database"""
app = Flask(__name__)
app.config['SQLALCHEMY_DATABASE_URI'] = 'postgresql:///blogly'
app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False
app.config['SQLALCHEMY_ECHO'] = True


"""This will connect, drop existing tables, 
   and add new tables based on model
"""
connect_db(app)
db.drop_all()
db.create_all()

jane = User(first_name='Calamity', last_name='Jane', profile_image="https://horseyhooves.com/wp-content/uploads/2020/09/Calamity-Jane-portrait.jpg")
james = User(first_name='Jesse', last_name='James', profile_image="data:image/jpeg;base64,")
pearl = User(first_name='Pearl', last_name='Heart')
pickett = User(first_name='Bill', last_name='Pickett', profile_image="https://upload.wikimedia.org/wikipedia/commons/5/5f/Bill_Picket_North_Fort_Worth_Historical_Society.jpg")

# Add new objects to session, so they'll persist
db.session.add_all([jane, james, pearl, pickett])

# Commit--otherwise, this never gets saved!
db.session.commit()

